class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComments(e);let t=this;$(".delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComments(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$("#post-comments-"+e).prepend(o),t.deleteComment($(".delete-comment-button"),o),new ToggleLike($(" .like-button"),o),new Noty({theme:"relax",text:"Comment created",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom=function(e){return $(`<li id="comment-${e._id}">\n          <a class="delete-comment-button" href="/comments/destroy/${e._id}">X</a>\n        <p>\n          ${e.content}\n          <br>\n          <small>\n            ${e.user.name}\n          </small>\n          <small>\n          <a calss="like-button" href="/likes/toggle_like/?id=${e._id}&type=Comment" data-likes="0">0 Likes</a>\n        </small>\n        </p>\n      </li>`)};deleteComment=function(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"Comment deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}